// Upon reload or redeploy, let's go through all the devices and check if
// they have not been seen in the last 65000ms, if so they are down and
// we will update the objects in memory

// Without this function, our objects will only show as down when there was
// previously a message that ran through the trigger node, which then timed-out.
// This doesn't cover the case where we redeployed and in the meantime
// something went down

var sonoffs = flow.get("DEVICES")
var d = new Date().toLocaleDateString();
var t = new Date().toLocaleTimeString();

Object.keys(sonoffs).forEach(Device => {
    if (Date.now() - Date.parse(sonoffs[Device].last_seen) > 65000) {
        if (sonoffs[Device].state == "up") {
            sonoffs[Device].state = "down"
            sonoffs[Device].down_since_date = d
            sonoffs[Device].down_since_time = t
        }
    }
    msg.bork = [sonoffs[Device].Device, sonoffs[Device].state]
}
)

